GenerateName.data:
function() {
    const startsByLetter = new Map();
    for (let start of this.nameStarts) {
        let letter = start[0];

        let starts = startsByLetter.get(letter) || [];
        starts.push(start);
        startsByLetter.set(letter, starts);
    }

    let firstLetter = World.Random.Choice(Array.from(startsByLetter.keys()));
    let name = World.Random.Choice(startsByLetter.get(firstLetter));

    let numParts = World.Random.Integer(1, 3);
    for(var i = 0; i < numParts; ++i) {
        name += World.Random.Choice(this.nameParts);
    }

    name = name[0].toUpperCase() + name.slice(1);
    return name;
}

