FindSlots.data:
function() {
  // Get all slots in objects tagged with the module.
  let visited = new Set();
  let slots = [];

  var findSlots = (object) => {
    if(!_IsProtoObject(object)) return;
    if(visited.has(object)) return;

    visited.add(object);
    for(let slot of object.GetSlotNames()) {
      if (_GetSlotAnnotation(object, slot, 'module') == this) {
        slots.push({object, slot});
      }
      findSlots(object[slot]);
    }
  }

  findSlots(World);
  return slots;
}

GenerateCode.data:
function() {
  let slots = this.FindSlots();

  let code = "";
  for (let {object, slot} of slots) {
    let path = this.TracePath(object);
    for(let i = 0; i < path.length; ++i) {
      let subpath = path.slice(0, i + 1);
      let objpath = `window.${subpath.join('.')}`;
      code += `${objpath} = ${objpath} || _EmptyObject();\n`;
    }

    let value = object[slot];
    let objpath = `window.${path.join('.')}`;
    if (_IsMessageHandler(value)) {
      let handlerCode = _GetMessageHandlerCode(value);
      let escapedCode = escapeTemplateString(handlerCode);
      code += `_AddSlot(${objpath}, "${slot}", _MakeMessageHandler(${escapedCode}));\n`
    } else if (_IsProtoObject(value)) {
        let creator = value.GetCreator();
        let isCreator = creator.object == object && creator.slot == slot;
        if (isCreator) {
          code += `if(!${objpath}.${slot}) _AddSlot(${objpath}, "${slot}", _EmptyObject());\n`;
          if(value.GetName())
            code += `${objpath}.${slot}.SetName(${escapeTemplateString(value.GetName())});\n`;
          if(value.GetDescription())
            code += `${objpath}.${slot}.SetDescription(${escapeTemplateString(value.GetDescription())});\n`;

          code += `${objpath}.${slot}.SetCreator(${objpath}, "${slot}");\n`;
        } else {
         code += `_AddSlot(${objpath}, "${slot}", window.${this.TracePath(value).join('.')});\n`
        }

    } else {
      throw new Error(`Encountered an object of Unknown type.`);
    }

    code += '\n';
  }

  return code;
}

