TracePath.data:
function(object) {
    if (object == World) return ['World'];
    else {
        let creator = _GetAnnotation(object, 'creator');
        if (!creator)
            throw new Error(`${object.ToString()} missing creator slot.`);
        if (creator.object[creator.slot] != object)
            throw new Error(`${object.ToString()}'s creator slot is invalid.`);

        var path = this.TracePath(creator.object);
        path.push(creator.slot);
        return path;
    }
}

